import React, { useState } from 'react';
import Input from '../components/common/Input';
import Button from '../components/common/Button';

const LoginScreen = ({ onLogin }) => {
  const [form, setForm] = useState({ phone: '', otp: '' });
  const [step, setStep] = useState('phone'); // phone | otp
  const [loading, setLoading] = useState(false);

  const handleSendOTP = () => {
    if (!form.phone || form.phone.length < 10) return;
    setLoading(true);
    setTimeout(() => { setLoading(false); setStep('otp'); }, 1500);
  };

  const handleVerify = () => {
    setLoading(true);
    setTimeout(() => { setLoading(false); onLogin?.(); }, 1500);
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: '#020408',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      position: 'relative',
      overflow: 'hidden',
      fontFamily: "'Rajdhani', sans-serif",
    }}>
      {/* Background grid */}
      <div style={{
        position: 'fixed', inset: 0,
        backgroundImage: `
          linear-gradient(rgba(0,245,255,0.03) 1px, transparent 1px),
          linear-gradient(90deg, rgba(0,245,255,0.03) 1px, transparent 1px)
        `,
        backgroundSize: '60px 60px',
        pointerEvents: 'none',
      }} />

      {/* Ambient glows */}
      <div style={{
        position: 'fixed', top: '-200px', left: '-200px',
        width: '600px', height: '600px',
        background: 'radial-gradient(circle, rgba(0,245,255,0.06) 0%, transparent 70%)',
        pointerEvents: 'none',
      }} />
      <div style={{
        position: 'fixed', bottom: '-200px', right: '-200px',
        width: '600px', height: '600px',
        background: 'radial-gradient(circle, rgba(255,45,120,0.06) 0%, transparent 70%)',
        pointerEvents: 'none',
      }} />

      <div style={{
        width: '100%', maxWidth: '420px',
        padding: '40px 32px',
        background: 'linear-gradient(145deg, #060d16 0%, #030810 100%)',
        border: '1px solid rgba(0,245,255,0.15)',
        borderRadius: '12px',
        boxShadow: '0 0 60px rgba(0,245,255,0.08), 0 30px 80px rgba(0,0,0,0.8)',
        position: 'relative',
        overflow: 'hidden',
        animation: 'slide-in-up 0.5s ease',
      }}>
        {/* Corner decorations */}
        {[
          { top: '12px', left: '12px', borderTop: '2px solid #00f5ff', borderLeft: '2px solid #00f5ff' },
          { top: '12px', right: '12px', borderTop: '2px solid #00f5ff', borderRight: '2px solid #00f5ff' },
          { bottom: '12px', left: '12px', borderBottom: '2px solid #00f5ff', borderLeft: '2px solid #00f5ff' },
          { bottom: '12px', right: '12px', borderBottom: '2px solid #00f5ff', borderRight: '2px solid #00f5ff' },
        ].map((s, i) => (
          <div key={i} style={{ position: 'absolute', width: '20px', height: '20px', opacity: 0.5, ...s }} />
        ))}

        {/* Top accent */}
        <div style={{
          position: 'absolute', top: 0, left: 0, right: 0, height: '2px',
          background: 'linear-gradient(90deg, transparent, #00f5ff, #ff2d78, transparent)',
        }} />

        {/* Logo */}
        <div style={{ textAlign: 'center', marginBottom: '36px' }}>
          <div style={{
            fontSize: '60px', marginBottom: '12px',
            filter: 'drop-shadow(0 0 16px rgba(0,245,255,0.6))',
            animation: 'float 3s ease-in-out infinite',
          }}>
            üö®
          </div>
          <div style={{
            fontFamily: "'Exo 2', sans-serif",
            fontSize: '26px', fontWeight: '900',
            letterSpacing: '4px', textTransform: 'uppercase',
            background: 'linear-gradient(135deg, #00f5ff 0%, #ff2d78 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text',
            marginBottom: '4px',
          }}>
            ResQRoute
          </div>
          <div style={{
            fontFamily: "'Share Tech Mono', monospace",
            fontSize: '11px', color: '#2a4a6a', letterSpacing: '3px',
          }}>
            SMART EMERGENCY RESPONSE
          </div>
        </div>

        {/* Step indicator */}
        <div style={{ display: 'flex', gap: '8px', marginBottom: '28px' }}>
          {['Enter Mobile', 'Verify OTP'].map((s, i) => (
            <div key={i} style={{ flex: 1 }}>
              <div style={{
                height: '3px', borderRadius: '2px',
                background: i === 0
                  ? (step === 'phone' || step === 'otp' ? '#00f5ff' : 'rgba(0,245,255,0.2)')
                  : (step === 'otp' ? '#00f5ff' : 'rgba(0,245,255,0.2)'),
                boxShadow: (i === 0 || step === 'otp') ? '0 0 8px rgba(0,245,255,0.5)' : 'none',
                marginBottom: '6px',
                transition: 'all 0.3s',
              }} />
              <div style={{
                fontSize: '10px', letterSpacing: '1.5px',
                textTransform: 'uppercase', fontFamily: "'Exo 2', sans-serif",
                color: (i === 0 || step === 'otp') ? '#00f5ff' : '#2a4a6a',
                transition: 'color 0.3s',
              }}>
                {s}
              </div>
            </div>
          ))}
        </div>

        {step === 'phone' ? (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
            <Input
              label="Mobile Number"
              type="tel"
              value={form.phone}
              onChange={v => setForm(f => ({ ...f, phone: v }))}
              placeholder="+91 XXXXX XXXXX"
              required
              icon="üì±"
            />
            <Button onClick={handleSendOTP} fullWidth size="lg" loading={loading}>
              SEND OTP
            </Button>
          </div>
        ) : (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
            <div style={{ textAlign: 'center', fontSize: '13px', color: '#5a7a9a', marginBottom: '4px' }}>
              OTP sent to <span style={{ color: '#00f5ff' }}>+91 {form.phone}</span>
            </div>
            <Input
              label="Enter OTP"
              type="number"
              value={form.otp}
              onChange={v => setForm(f => ({ ...f, otp: v }))}
              placeholder="6-digit code"
              required
              icon="üîê"
            />
            <Button onClick={handleVerify} fullWidth size="lg" loading={loading}>
              VERIFY & ACCESS
            </Button>
            <Button variant="ghost" onClick={() => setStep('phone')} fullWidth size="sm">
              ‚Üê Change Number
            </Button>
          </div>
        )}

        <div style={{
          marginTop: '28px', paddingTop: '20px',
          borderTop: '1px solid rgba(0,245,255,0.08)',
          textAlign: 'center',
          fontSize: '11px', color: '#2a4a6a',
          fontFamily: "'Share Tech Mono', monospace",
          letterSpacing: '1px',
        }}>
          SECURED BY ANTHROPIC AI ¬∑ GOVERNMENT CERTIFIED
        </div>
      </div>
    </div>
  );
};

export default LoginScreen;
