import React, { useState } from 'react';
import { BgGrid, Button, Card, StepBar, NavBar, Input, SummaryRow } from './SharedComponents';

const steps = [
  { id: 'emergency', label: 'Emergency', icon: 'âš¡' },
  { id: 'patient',   label: 'Patient',   icon: 'ðŸ‘¤' },
  { id: 'vehicle',   label: 'Vehicle',   icon: 'ðŸš‘' },
  { id: 'hospital',  label: 'Hospital',  icon: 'ðŸ¥' },
  { id: 'location',  label: 'Location',  icon: 'ðŸ“' },
  { id: 'confirm',   label: 'Confirm',   icon: 'âœ…' },
];

const emergencyTypes = [
  { id:'cardiac', label:'Cardiac Arrest', icon:'â¤ï¸' },
  { id:'accident', label:'Accident/Trauma', icon:'ðŸ¤•' },
  { id:'stroke', label:'Stroke', icon:'ðŸ§ ' },
  { id:'respiratory', label:'Breathing Issue', icon:'ðŸ«' },
  { id:'labour', label:'Labour/Delivery', icon:'ðŸ¤°' },
  { id:'other', label:'Other Medical', icon:'ðŸ¥' },
];

const vehicleTypes = [
  { id:'als', label:'ALS Ambulance', desc:'Advanced Life Support Â· ICU equipped', icon:'ðŸš‘' },
  { id:'bls', label:'BLS Ambulance', desc:'Basic Life Support Â· Standard', icon:'ðŸ¥' },
  { id:'neonatal', label:'Neonatal', desc:'Newborn & infant critical care', icon:'ðŸ‘¶' },
];

const SelectGrid = ({ options, selected, onSelect, color='#00f5ff', rgb='0,245,255' }) => (
  <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fill, minmax(140px, 1fr))', gap:'10px' }}>
    {options.map(opt => {
      const isSelected = selected === opt.id;
      return (
        <div
          key={opt.id}
          onClick={() => onSelect(opt.id)}
          style={{
            padding:'14px 12px', borderRadius:'8px', cursor:'pointer', textAlign:'center',
            background: isSelected ? `rgba(${rgb},0.12)` : 'rgba(4,9,15,0.7)',
            border: `1px solid ${isSelected ? color : `rgba(${rgb},0.18)`}`,
            boxShadow: isSelected ? `0 0 20px rgba(${rgb},0.2)` : 'none',
            transition:'all 0.2s',
          }}
        >
          <div style={{ fontSize:'28px', marginBottom:'8px', lineHeight:1 }}>{opt.icon}</div>
          <div style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'11px', fontWeight:'700', color: isSelected ? color : '#e8f4fd', letterSpacing:'1px', textTransform:'uppercase', lineHeight:'1.3' }}>
            {opt.label}
          </div>
          {opt.desc && (
            <div style={{ fontSize:'10px', color:'#5a7a9a', marginTop:'4px', lineHeight:'1.3' }}>{opt.desc}</div>
          )}
        </div>
      );
    })}
  </div>
);

const MedicalBookingScreen = ({ onNavigate }) => {
  const [currentStep, setCurrentStep] = useState(0);
  const [booking, setBooking] = useState({
    emergencyType: null,
    patient: { name:'', age:'', gender:'', contact:'' },
    vehicleType: null,
    hospitalPreference: null,
    location: { address:'' },
  });
  const [loading, setLoading] = useState(false);

  const update = (key, val) => setBooking(b => ({ ...b, [key]: val }));
  const updatePatient = (key, val) => setBooking(b => ({ ...b, patient: { ...b.patient, [key]: val } }));

  const canProceed = () => {
    switch (steps[currentStep].id) {
      case 'emergency': return !!booking.emergencyType;
      case 'patient':   return !!booking.patient.name && !!booking.patient.age;
      case 'vehicle':   return !!booking.vehicleType;
      case 'hospital':  return !!booking.hospitalPreference;
      case 'location':  return !!booking.location.address;
      default: return true;
    }
  };

  const handleBook = () => {
    setLoading(true);
    setTimeout(() => { setLoading(false); onNavigate('tracking', { type:'medical', booking }); }, 2000);
  };

  const StepContent = () => {
    switch (steps[currentStep].id) {

      case 'emergency':
        return (
          <div style={{ display:'flex', flexDirection:'column', gap:'16px' }}>
            <p style={{ fontSize:'13px', color:'#5a7a9a' }}>Select the nature of the medical emergency</p>
            <SelectGrid options={emergencyTypes} selected={booking.emergencyType} onSelect={v => update('emergencyType', v)} />
          </div>
        );

      case 'patient':
        return (
          <div style={{ display:'flex', flexDirection:'column', gap:'16px' }}>
            <div style={{ display:'grid', gridTemplateColumns:'1fr 1fr', gap:'14px' }}>
              <Input style={{ gridColumn:'1/-1' }} label="Patient Name" type="text" value={booking.patient.name} onChange={v => updatePatient('name', v)} placeholder="Full name" icon="ðŸ‘¤" />
              <Input label="Age (years)" type="number" value={booking.patient.age} onChange={v => updatePatient('age', v)} placeholder="Age" icon="ðŸŽ‚" />
              <Input label="Contact No." type="tel" value={booking.patient.contact} onChange={v => updatePatient('contact', v)} placeholder="+91 XXXXX" icon="ðŸ“ž" />
            </div>
            <div>
              <div style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'11px', letterSpacing:'2px', textTransform:'uppercase', color:'rgba(0,245,255,0.6)', marginBottom:'10px' }}>Gender</div>
              <div style={{ display:'flex', gap:'10px', flexWrap:'wrap' }}>
                {['Male','Female','Other'].map(g => (
                  <button key={g} onClick={() => updatePatient('gender', g)} style={{
                    padding:'9px 18px', borderRadius:'6px', cursor:'pointer',
                    background: booking.patient.gender === g ? 'rgba(0,245,255,0.12)' : 'rgba(4,9,15,0.7)',
                    border: `1px solid ${booking.patient.gender === g ? '#00f5ff' : 'rgba(0,245,255,0.2)'}`,
                    color: booking.patient.gender === g ? '#00f5ff' : '#e8f4fd',
                    fontFamily:"'Exo 2', sans-serif", fontSize:'12px', fontWeight:'700', letterSpacing:'1.5px',
                    transition:'all 0.2s',
                  }}>{g}</button>
                ))}
              </div>
            </div>
          </div>
        );

      case 'vehicle':
        return (
          <div style={{ display:'flex', flexDirection:'column', gap:'12px' }}>
            <p style={{ fontSize:'13px', color:'#5a7a9a' }}>Choose vehicle type based on patient condition</p>
            {vehicleTypes.map(v => {
              const isSelected = booking.vehicleType === v.id;
              return (
                <div key={v.id} onClick={() => update('vehicleType', v.id)} style={{
                  padding:'16px 18px', borderRadius:'8px', cursor:'pointer',
                  background: isSelected ? 'rgba(0,245,255,0.08)' : 'rgba(4,9,15,0.7)',
                  border: `1px solid ${isSelected ? '#00f5ff' : 'rgba(0,245,255,0.15)'}`,
                  display:'flex', alignItems:'center', gap:'14px',
                  transition:'all 0.2s',
                  boxShadow: isSelected ? '0 0 20px rgba(0,245,255,0.15)' : 'none',
                }}>
                  <div style={{ fontSize:'32px', lineHeight:1, flexShrink:0 }}>{v.icon}</div>
                  <div style={{ flex:1, minWidth:0 }}>
                    <div style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'14px', fontWeight:'700', color: isSelected ? '#00f5ff' : '#e8f4fd', letterSpacing:'1.5px' }}>{v.label}</div>
                    <div style={{ fontSize:'12px', color:'#5a7a9a', marginTop:'2px' }}>{v.desc}</div>
                  </div>
                  {isSelected && <div style={{ width:'10px', height:'10px', background:'#00f5ff', borderRadius:'50%', boxShadow:'0 0 8px #00f5ff', flexShrink:0 }} />}
                </div>
              );
            })}
          </div>
        );

      case 'hospital':
        return (
          <div style={{ display:'flex', flexDirection:'column', gap:'16px' }}>
            <p style={{ fontSize:'13px', color:'#5a7a9a' }}>Choose hospital preference</p>
            <div style={{ display:'grid', gridTemplateColumns:'1fr 1fr', gap:'12px' }}>
              {[
                { id:'government', label:'Government', sub:'FREE Â· AIIMS Â· Safdarjung', icon:'ðŸ›ï¸', color:'#7fff00', rgb:'127,255,0' },
                { id:'private', label:'Private', sub:'Premium Â· Faster beds', icon:'ðŸ¥', color:'#00f5ff', rgb:'0,245,255' },
              ].map(h => {
                const isSelected = booking.hospitalPreference === h.id;
                return (
                  <div key={h.id} onClick={() => update('hospitalPreference', h.id)} style={{
                    padding:'20px 16px', borderRadius:'8px', cursor:'pointer', textAlign:'center',
                    background: isSelected ? `rgba(${h.rgb},0.1)` : 'rgba(4,9,15,0.7)',
                    border: `2px solid ${isSelected ? h.color : `rgba(${h.rgb},0.2)`}`,
                    transition:'all 0.2s',
                  }}>
                    <div style={{ fontSize:'36px', lineHeight:1, marginBottom:'10px' }}>{h.icon}</div>
                    <div style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'14px', fontWeight:'800', color: isSelected ? h.color : '#e8f4fd', letterSpacing:'2px', marginBottom:'4px' }}>{h.label}</div>
                    <div style={{ fontSize:'11px', color:'#5a7a9a' }}>{h.sub}</div>
                  </div>
                );
              })}
            </div>
            {booking.hospitalPreference === 'government' && (
              <div style={{ padding:'14px', background:'rgba(127,255,0,0.06)', border:'1px solid rgba(127,255,0,0.2)', borderRadius:'8px', fontSize:'13px', color:'#7fff00', lineHeight:'1.5' }}>
                âœ… Auto-routed to nearest government hospital with available beds (AIIMS, Safdarjung, GTB)
              </div>
            )}
          </div>
        );

      case 'location':
        return (
          <div style={{ display:'flex', flexDirection:'column', gap:'16px' }}>
            <p style={{ fontSize:'13px', color:'#5a7a9a' }}>Enter the pickup location for the patient</p>
            <Input label="Pickup Address" type="text" value={booking.location.address} onChange={v => update('location', { address:v })} placeholder="House no, street, landmark, city" icon="ðŸ“" />
            <div style={{ padding:'14px', background:'rgba(0,245,255,0.04)', border:'1px solid rgba(0,245,255,0.1)', borderRadius:'8px', fontSize:'12px', color:'#5a7a9a', lineHeight:'1.5', fontFamily:"'Share Tech Mono', monospace", letterSpacing:'0.5px' }}>
              ðŸ’¡ TIP: Add a nearby landmark for faster dispatch routing
            </div>
          </div>
        );

      case 'confirm':
        return (
          <div style={{ display:'flex', flexDirection:'column', gap:'16px' }}>
            <div style={{ padding:'16px', background:'rgba(0,245,255,0.05)', border:'1px solid rgba(0,245,255,0.2)', borderRadius:'8px' }}>
              <div style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'12px', color:'#00f5ff', letterSpacing:'2px', marginBottom:'14px' }}>â—† BOOKING SUMMARY</div>
              <SummaryRow label="Emergency" value={booking.emergencyType?.toUpperCase() || 'â€”'} />
              <SummaryRow label="Patient" value={booking.patient?.name || 'â€”'} />
              <SummaryRow label="Age" value={booking.patient?.age ? `${booking.patient.age} yrs` : 'â€”'} />
              <SummaryRow label="Vehicle" value={booking.vehicleType?.toUpperCase() || 'â€”'} />
              <SummaryRow label="Hospital" value={booking.hospitalPreference === 'government' ? 'AIIMS (FREE)' : 'Private Hospital'} />
              <SummaryRow label="Location" value={booking.location?.address || 'â€”'} />
            </div>
            <div style={{ padding:'14px', background:'rgba(127,255,0,0.06)', border:'1px solid rgba(127,255,0,0.2)', borderRadius:'6px', fontSize:'13px', color:'#7fff00', lineHeight:'1.5' }}>
              âœ… Nearest ambulance will be dispatched immediately. Avg response: <strong>6-8 minutes</strong>.
            </div>
            <Button onClick={handleBook} fullWidth size="lg" loading={loading}>
              ðŸš‘ DISPATCH AMBULANCE NOW
            </Button>
          </div>
        );

      default: return null;
    }
  };

  return (
    <>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Exo+2:wght@700;800;900&family=Share+Tech+Mono&family=Rajdhani:wght@400;500;600;700&display=swap');
        *, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }
        @keyframes slideUp { from{opacity:0;transform:translateY(16px)} to{opacity:1;transform:translateY(0)} }
        @keyframes spin { from{transform:rotate(0deg)} to{transform:rotate(360deg)} }
        input { -webkit-appearance: none; }
        input[type=number]::-webkit-inner-spin-button,
        input[type=number]::-webkit-outer-spin-button { -webkit-appearance: none; }
        input[type=number] { -moz-appearance: textfield; }
      `}</style>

      <div style={{ minHeight:'100vh', minHeight:'100dvh', background:'#020408', color:'#e8f4fd', fontFamily:"'Rajdhani', sans-serif", position:'relative' }}>
        <BgGrid />
        <NavBar
          onBack={() => currentStep > 0 ? setCurrentStep(s => s - 1) : onNavigate('home')}
          title="Medical Emergency Booking"
          subtitle={`STEP ${currentStep + 1} OF ${steps.length} Â· ${steps[currentStep].label.toUpperCase()}`}
        />

        <div style={{ maxWidth:'680px', margin:'0 auto', padding:'clamp(20px,4vw,32px) clamp(16px,4vw,24px)', position:'relative', zIndex:1 }}>
          <StepBar steps={steps} currentStep={currentStep} />
          <Card title={steps[currentStep].label} glow="primary" style={{ marginBottom:'20px', animation:'slideUp 0.3s ease' }}>
            <StepContent />
          </Card>

          {steps[currentStep].id !== 'confirm' && (
            <div style={{ display:'flex', justifyContent:'flex-end' }}>
              <Button onClick={() => setCurrentStep(s => s + 1)} size="lg" disabled={!canProceed()}>
                NEXT: {steps[currentStep + 1]?.label?.toUpperCase()} â†’
              </Button>
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default MedicalBookingScreen;
