import React, { useEffect, useRef, useState } from 'react';

// ‚îÄ‚îÄ‚îÄ LiveMap Component ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
export const LiveMap = ({ booking, vehiclePosition, type = 'medical' }) => {
  const [pulse, setPulse] = useState(false);
  const accentColor = type === 'fire' ? '#ff2d78' : '#00f5ff';
  const accentRGB = type === 'fire' ? '255,45,120' : '0,245,255';

  useEffect(() => {
    const interval = setInterval(() => setPulse(p => !p), 1500);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{
      position: 'relative',
      width: '100%',
      height: '320px',
      background: 'linear-gradient(145deg, #020408 0%, #050c1a 100%)',
      border: `1px solid rgba(${accentRGB},0.2)`,
      borderRadius: '8px',
      overflow: 'hidden',
    }}>
      {/* Grid overlay (simulated map) */}
      <svg width="100%" height="100%" style={{ position: 'absolute', top: 0, left: 0, opacity: 0.08 }}>
        <defs>
          <pattern id="grid" width="40" height="40" patternUnits="userSpaceOnUse">
            <path d="M 40 0 L 0 0 0 40" fill="none" stroke={accentColor} strokeWidth="0.5" />
          </pattern>
        </defs>
        <rect width="100%" height="100%" fill="url(#grid)" />
      </svg>

      {/* Road lines simulation */}
      <svg width="100%" height="100%" style={{ position: 'absolute', top: 0, left: 0, opacity: 0.15 }}>
        <line x1="0" y1="160" x2="100%" y2="160" stroke={accentColor} strokeWidth="2" />
        <line x1="50%" y1="0" x2="50%" y2="100%" stroke={accentColor} strokeWidth="1.5" />
        <line x1="25%" y1="0" x2="25%" y2="100%" stroke={accentColor} strokeWidth="0.8" />
        <line x1="75%" y1="0" x2="75%" y2="100%" stroke={accentColor} strokeWidth="0.8" />
        <line x1="0" y1="80" x2="100%" y2="80" stroke={accentColor} strokeWidth="0.8" />
        <line x1="0" y1="240" x2="100%" y2="240" stroke={accentColor} strokeWidth="0.8" />
      </svg>

      {/* Route path */}
      <svg width="100%" height="100%" style={{ position: 'absolute', top: 0, left: 0 }}>
        <defs>
          <filter id="glow">
            <feGaussianBlur stdDeviation="3" result="blur" />
            <feMerge>
              <feMergeNode in="blur" />
              <feMergeNode in="SourceGraphic" />
            </feMerge>
          </filter>
        </defs>
        <path
          d="M 80 240 L 80 160 L 350 160 L 350 100 L 520 100"
          fill="none"
          stroke={accentColor}
          strokeWidth="3"
          strokeDasharray="8 4"
          filter="url(#glow)"
          opacity="0.7"
        />
      </svg>

      {/* Emergency origin (blinking) */}
      <div style={{
        position: 'absolute', top: '218px', left: '58px',
        transform: 'translate(-50%, -50%)',
      }}>
        <div style={{
          width: '16px', height: '16px',
          background: '#ff2d78',
          borderRadius: '50%',
          boxShadow: pulse ? '0 0 20px #ff2d78, 0 0 40px rgba(255,45,120,0.5)' : '0 0 8px #ff2d78',
          transition: 'box-shadow 0.5s',
          animation: 'pulse-neon 1.5s ease infinite',
        }} />
        <div style={{
          position: 'absolute', top: '20px', left: '50%',
          transform: 'translateX(-50%)',
          background: 'rgba(255,45,120,0.9)',
          color: '#fff',
          fontSize: '9px',
          fontWeight: '700',
          letterSpacing: '1px',
          padding: '2px 6px',
          borderRadius: '2px',
          fontFamily: "'Exo 2', sans-serif",
          whiteSpace: 'nowrap',
        }}>
          üìç YOUR LOCATION
        </div>
      </div>

      {/* Vehicle marker */}
      <div style={{
