import React, { useState } from 'react';

/* ‚îÄ‚îÄ Inline Input ‚îÄ‚îÄ */
const Input = ({ label, type = 'text', value, onChange, placeholder, icon }) => (
  <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
    <label style={{
      fontFamily: "'Exo 2', sans-serif",
      fontSize: '11px', letterSpacing: '2px',
      textTransform: 'uppercase', color: '#00f5ff', opacity: 0.8,
    }}>
      {label}
    </label>
    <div style={{ position: 'relative' }}>
      {icon && (
        <span style={{
          position: 'absolute', left: '14px', top: '50%',
          transform: 'translateY(-50%)', fontSize: '16px', pointerEvents: 'none',
        }}>
          {icon}
        </span>
      )}
      <input
        type={type}
        value={value}
        placeholder={placeholder}
        onChange={e => onChange(e.target.value)}
        inputMode={type === 'tel' ? 'tel' : type === 'number' ? 'numeric' : 'text'}
        style={{
          width: '100%',
          boxSizing: 'border-box',
          padding: icon ? '14px 16px 14px 44px' : '14px 16px',
          background: 'rgba(0,245,255,0.04)',
          border: '1px solid rgba(0,245,255,0.2)',
          borderRadius: '8px',
          color: '#e0f7ff',
          fontSize: '16px',            /* prevents iOS zoom */
          fontFamily: "'Share Tech Mono', monospace",
          letterSpacing: '2px',
          outline: 'none',
          transition: 'border-color 0.2s, box-shadow 0.2s',
          WebkitAppearance: 'none',
        }}
        onFocus={e => {
          e.target.style.borderColor = 'rgba(0,245,255,0.6)';
          e.target.style.boxShadow = '0 0 12px rgba(0,245,255,0.15)';
        }}
        onBlur={e => {
          e.target.style.borderColor = 'rgba(0,245,255,0.2)';
          e.target.style.boxShadow = 'none';
        }}
      />
    </div>
  </div>
);

/* ‚îÄ‚îÄ Inline Button ‚îÄ‚îÄ */
const Button = ({ children, onClick, fullWidth, size = 'md', loading, variant = 'primary' }) => {
  const isGhost = variant === 'ghost';
  return (
    <button
      onClick={onClick}
      disabled={loading}
      style={{
        width: fullWidth ? '100%' : 'auto',
        padding: size === 'lg' ? '15px 28px' : '10px 20px',
        background: isGhost
          ? 'transparent'
          : 'linear-gradient(135deg, rgba(0,245,255,0.15) 0%, rgba(255,45,120,0.1) 100%)',
        border: isGhost
          ? '1px solid rgba(0,245,255,0.15)'
          : '1px solid rgba(0,245,255,0.4)',
        borderRadius: '8px',
        color: isGhost ? 'rgba(0,245,255,0.5)' : '#00f5ff',
        fontSize: size === 'lg' ? '14px' : '12px',
        fontFamily: "'Exo 2', sans-serif",
        fontWeight: '700',
        letterSpacing: '3px',
        textTransform: 'uppercase',
        cursor: loading ? 'not-allowed' : 'pointer',
        opacity: loading ? 0.6 : 1,
        transition: 'all 0.2s',
        boxShadow: isGhost ? 'none' : '0 0 20px rgba(0,245,255,0.1)',
      }}
      onMouseEnter={e => {
        if (!loading && !isGhost) e.currentTarget.style.boxShadow = '0 0 30px rgba(0,245,255,0.25)';
      }}
      onMouseLeave={e => {
        if (!isGhost) e.currentTarget.style.boxShadow = '0 0 20px rgba(0,245,255,0.1)';
      }}
    >
      {loading ? '...' : children}
    </button>
  );
};

/* ‚îÄ‚îÄ Main Screen ‚îÄ‚îÄ */
const LoginScreen = ({ onLogin }) => {
  const [phone, setPhone] = useState('');
  const [otp, setOtp] = useState('');
  const [step, setStep] = useState('phone');
  const [loading, setLoading] = useState(false);

  const handleSendOTP = () => {
    if (!phone || phone.replace(/\D/g, '').length < 10) return;
    setLoading(true);
    setTimeout(() => { setLoading(false); setStep('otp'); }, 1500);
  };

  const handleVerify = () => {
    setLoading(true);
    setTimeout(() => { setLoading(false); onLogin?.(); }, 1500);
  };

  return (
    <>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Exo+2:wght@400;700;900&family=Share+Tech+Mono&family=Rajdhani:wght@500;700&display=swap');
        @keyframes float { 0%,100%{transform:translateY(0)} 50%{transform:translateY(-8px)} }
        @keyframes slideUp { from{opacity:0;transform:translateY(24px)} to{opacity:1;transform:translateY(0)} }
        input[type=number]::-webkit-inner-spin-button,
        input[type=number]::-webkit-outer-spin-button { -webkit-appearance:none; margin:0; }
        input[type=number] { -moz-appearance: textfield; }
        * { box-sizing: border-box; }
      `}</style>

      <div style={{
        minHeight: '100vh',
        background: '#020408',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '20px',
        fontFamily: "'Rajdhani', sans-serif",
        position: 'relative',
        overflow: 'hidden',
      }}>
        {/* Grid background */}
        <div style={{
          position: 'fixed', inset: 0,
          backgroundImage: `
            linear-gradient(rgba(0,245,255,0.03) 1px, transparent 1px),
            linear-gradient(90deg, rgba(0,245,255,0.03) 1px, transparent 1px)
          `,
          backgroundSize: '60px 60px',
          pointerEvents: 'none',
        }} />

        {/* Glows */}
        <div style={{ position:'fixed', top:'-200px', left:'-200px', width:'600px', height:'600px',
          background:'radial-gradient(circle, rgba(0,245,255,0.06) 0%, transparent 70%)', pointerEvents:'none' }} />
        <div style={{ position:'fixed', bottom:'-200px', right:'-200px', width:'600px', height:'600px',
          background:'radial-gradient(circle, rgba(255,45,120,0.06) 0%, transparent 70%)', pointerEvents:'none' }} />

        {/* Card */}
        <div style={{
          width: '100%', maxWidth: '420px',
          padding: '40px 32px',
          background: 'linear-gradient(145deg, #060d16 0%, #030810 100%)',
          border: '1px solid rgba(0,245,255,0.15)',
          borderRadius: '12px',
          boxShadow: '0 0 60px rgba(0,245,255,0.08), 0 30px 80px rgba(0,0,0,0.8)',
          position: 'relative',
          overflow: 'hidden',
          animation: 'slideUp 0.5s ease',
        }}>
          {/* Corner decorations */}
          {[
            { top:'12px', left:'12px', borderTop:'2px solid #00f5ff', borderLeft:'2px solid #00f5ff' },
            { top:'12px', right:'12px', borderTop:'2px solid #00f5ff', borderRight:'2px solid #00f5ff' },
            { bottom:'12px', left:'12px', borderBottom:'2px solid #00f5ff', borderLeft:'2px solid #00f5ff' },
            { bottom:'12px', right:'12px', borderBottom:'2px solid #00f5ff', borderRight:'2px solid #00f5ff' },
          ].map((s, i) => (
            <div key={i} style={{ position:'absolute', width:'20px', height:'20px', opacity:0.5, ...s }} />
          ))}

          {/* Top accent */}
          <div style={{
            position:'absolute', top:0, left:0, right:0, height:'2px',
            background:'linear-gradient(90deg, transparent, #00f5ff, #ff2d78, transparent)',
          }} />

          {/* Logo */}
          <div style={{ textAlign:'center', marginBottom:'36px' }}>
            <div style={{
              fontSize:'60px', marginBottom:'12px',
              filter:'drop-shadow(0 0 16px rgba(0,245,255,0.6))',
              animation:'float 3s ease-in-out infinite',
            }}>üö®</div>
            <div style={{
              fontFamily:"'Exo 2', sans-serif",
              fontSize:'26px', fontWeight:'900',
              letterSpacing:'4px', textTransform:'uppercase',
              background:'linear-gradient(135deg, #00f5ff 0%, #ff2d78 100%)',
              WebkitBackgroundClip:'text',
              WebkitTextFillColor:'transparent',
              backgroundClip:'text',
              marginBottom:'4px',
            }}>ResQRoute</div>
            <div style={{
              fontFamily:"'Share Tech Mono', monospace",
              fontSize:'11px', color:'#2a4a6a', letterSpacing:'3px',
            }}>SMART EMERGENCY RESPONSE</div>
          </div>

          {/* Step indicator */}
          <div style={{ display:'flex', gap:'8px', marginBottom:'28px' }}>
            {['Enter Mobile', 'Verify OTP'].map((s, i) => (
              <div key={i} style={{ flex:1 }}>
                <div style={{
                  height:'3px', borderRadius:'2px',
                  background: (i === 0 || step === 'otp') ? '#00f5ff' : 'rgba(0,245,255,0.2)',
                  boxShadow: (i === 0 || step === 'otp') ? '0 0 8px rgba(0,245,255,0.5)' : 'none',
                  marginBottom:'6px', transition:'all 0.3s',
                }} />
                <div style={{
                  fontSize:'10px', letterSpacing:'1.5px',
                  textTransform:'uppercase', fontFamily:"'Exo 2', sans-serif",
                  color: (i === 0 || step === 'otp') ? '#00f5ff' : '#2a4a6a',
                  transition:'color 0.3s',
                }}>{s}</div>
              </div>
            ))}
          </div>

          {step === 'phone' ? (
            <div style={{ display:'flex', flexDirection:'column', gap:'20px' }}>
              <Input
                label="Mobile Number"
                type="tel"
                value={phone}
                onChange={setPhone}
                placeholder="+91 XXXXX XXXXX"
                icon="üì±"
              />
              <Button onClick={handleSendOTP} fullWidth size="lg" loading={loading}>
                SEND OTP
              </Button>
            </div>
          ) : (
            <div style={{ display:'flex', flexDirection:'column', gap:'20px' }}>
              <div style={{ textAlign:'center', fontSize:'13px', color:'#5a7a9a', marginBottom:'4px' }}>
                OTP sent to <span style={{ color:'#00f5ff' }}>+91 {phone}</span>
              </div>
              <Input
                label="Enter OTP"
                type="number"
                value={otp}
                onChange={setOtp}
                placeholder="6-digit code"
                icon="üîê"
              />
              <Button onClick={handleVerify} fullWidth size="lg" loading={loading}>
                VERIFY & ACCESS
              </Button>
              <Button variant="ghost" onClick={() => setStep('phone')} fullWidth size="sm">
                ‚Üê Change Number
              </Button>
            </div>
          )}

          <div style={{
            marginTop:'28px', paddingTop:'20px',
            borderTop:'1px solid rgba(0,245,255,0.08)',
            textAlign:'center',
            fontSize:'11px', color:'#2a4a6a',
            fontFamily:"'Share Tech Mono', monospace",
            letterSpacing:'1px',
          }}>
            SECURED BY ANTHROPIC AI ¬∑ GOVERNMENT CERTIFIED
          </div>
        </div>
      </div>
    </>
  );
};

export default LoginScreen;
