import React, { useState } from 'react';
import { BgGrid, Button, Card, StepBar, NavBar, Input, SummaryRow } from './SharedComponents';

const steps = [
  { id:'type',    label:'Fire Type', icon:'ðŸ”¥' },
  { id:'details', label:'Details',   icon:'ðŸ“‹' },
  { id:'building',label:'Building',  icon:'ðŸ¢' },
  { id:'vehicle', label:'Vehicles',  icon:'ðŸš’' },
  { id:'location',label:'Location',  icon:'ðŸ“' },
  { id:'confirm', label:'Dispatch',  icon:'ðŸ†˜' },
];

const fireTypes = [
  { id:'residential', label:'Residential', icon:'ðŸ ' },
  { id:'commercial',  label:'Commercial',  icon:'ðŸ¢' },
  { id:'industrial',  label:'Industrial',  icon:'ðŸ­' },
  { id:'vehicle',     label:'Vehicle',     icon:'ðŸš—' },
  { id:'forest',      label:'Forest/Open', icon:'ðŸŒ²' },
  { id:'hazmat',      label:'HazMat/Chemical', icon:'â˜¢ï¸' },
];

const scales = [
  { id:'small',  label:'Small',  desc:'Contained, 1-2 rooms', icon:'ðŸ”¸' },
  { id:'medium', label:'Medium', desc:'Spreading, 1 floor',   icon:'ðŸ”¶' },
  { id:'major',  label:'Major',  desc:'Entire building',      icon:'ðŸ”´' },
];

const buildingTypes = [
  { id:'apartment', label:'Apartment',  icon:'ðŸ˜ï¸' },
  { id:'house',     label:'House',      icon:'ðŸ¡' },
  { id:'office',    label:'Office',     icon:'ðŸ¢' },
  { id:'factory',   label:'Factory',    icon:'ðŸ­' },
  { id:'mall',      label:'Mall/Plaza', icon:'ðŸ¬' },
  { id:'other',     label:'Other',      icon:'ðŸ—ï¸' },
];

const SelectGrid = ({ options, selected, onSelect, color='#ff2d78', rgb='255,45,120' }) => (
  <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fill, minmax(130px, 1fr))', gap:'10px' }}>
    {options.map(opt => {
      const isSel = selected === opt.id;
      return (
        <div key={opt.id} onClick={() => onSelect(opt.id)} style={{
          padding:'14px 10px', borderRadius:'8px', cursor:'pointer', textAlign:'center',
          background: isSel ? `rgba(${rgb},0.12)` : 'rgba(4,9,15,0.7)',
          border: `1px solid ${isSel ? color : `rgba(${rgb},0.18)`}`,
          boxShadow: isSel ? `0 0 20px rgba(${rgb},0.2)` : 'none',
          transition:'all 0.2s',
        }}>
          <div style={{ fontSize:'26px', marginBottom:'7px', lineHeight:1 }}>{opt.icon}</div>
          <div style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'11px', fontWeight:'700', color: isSel ? color : '#e8f4fd', letterSpacing:'1px', textTransform:'uppercase', lineHeight:'1.3' }}>{opt.label}</div>
          {opt.desc && <div style={{ fontSize:'10px', color:'#5a7a9a', marginTop:'4px', lineHeight:'1.3' }}>{opt.desc}</div>}
        </div>
      );
    })}
  </div>
);

const FireBookingScreen = ({ onNavigate }) => {
  const [currentStep, setCurrentStep] = useState(0);
  const [booking, setBooking] = useState({
    fireType: null, scale: null, trapped:'0', building:{ type:null, floors:'' },
    vehicleType: null, location:{ address:'' },
  });
  const [loading, setLoading] = useState(false);

  const update = (key, val) => setBooking(b => ({ ...b, [key]: val }));
  const updateBuilding = (key, val) => setBooking(b => ({ ...b, building:{ ...b.building, [key]: val } }));

  const handleDispatch = () => {
    setLoading(true);
    setTimeout(() => { setLoading(false); onNavigate('tracking', { type:'fire', booking }); }, 2000);
  };

  const StepContent = () => {
    switch (steps[currentStep].id) {

      case 'type':
        return (
          <div style={{ display:'flex', flexDirection:'column', gap:'16px' }}>
            <p style={{ fontSize:'13px', color:'#5a7a9a' }}>Select the type of fire emergency</p>
            <SelectGrid options={fireTypes} selected={booking.fireType} onSelect={v => update('fireType', v)} />
          </div>
        );

      case 'details':
        return (
          <div style={{ display:'flex', flexDirection:'column', gap:'20px' }}>
            <div>
              <p style={{ fontSize:'13px', color:'#5a7a9a', marginBottom:'12px' }}>Fire scale / severity</p>
              <SelectGrid options={scales} selected={booking.scale} onSelect={v => update('scale', v)} />
            </div>
            <div>
              <div style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'11px', letterSpacing:'2px', textTransform:'uppercase', color:'rgba(255,45,120,0.7)', marginBottom:'8px' }}>
                People trapped / injured
              </div>
              <div style={{ display:'flex', gap:'10px', flexWrap:'wrap' }}>
                {['0','1-5','5-10','10+'].map(n => (
                  <button key={n} onClick={() => update('trapped', n)} style={{
                    padding:'9px 18px', borderRadius:'6px', cursor:'pointer',
                    background: booking.trapped === n ? 'rgba(255,45,120,0.12)' : 'rgba(4,9,15,0.7)',
                    border: `1px solid ${booking.trapped === n ? '#ff2d78' : 'rgba(255,45,120,0.2)'}`,
                    color: booking.trapped === n ? '#ff2d78' : '#e8f4fd',
                    fontFamily:"'Exo 2', sans-serif", fontSize:'12px', fontWeight:'700', letterSpacing:'1.5px',
                    transition:'all 0.2s',
                  }}>{n}</button>
                ))}
              </div>
            </div>
          </div>
        );

      case 'building':
        return (
          <div style={{ display:'flex', flexDirection:'column', gap:'20px' }}>
            <div>
              <p style={{ fontSize:'13px', color:'#5a7a9a', marginBottom:'12px' }}>Building type</p>
              <SelectGrid options={buildingTypes} selected={booking.building.type} onSelect={v => updateBuilding('type', v)} />
            </div>
            <Input label="Number of floors" type="number" value={booking.building.floors} onChange={v => updateBuilding('floors', v)} placeholder="e.g. 5" icon="ðŸ¢" />
          </div>
        );

      case 'vehicle':
        return (
          <div style={{ display:'flex', flexDirection:'column', gap:'12px' }}>
            <p style={{ fontSize:'13px', color:'#5a7a9a' }}>Select response unit combination</p>
            {[
              { id:'standard',  label:'Standard Response',     desc:'2 fire trucks Â· Standard water cannon', icon:'ðŸš’' },
              { id:'heavy',     label:'Heavy Response',        desc:'3 fire trucks + 1 rescue unit',         icon:'ðŸ”¥' },
              { id:'hazmat',    label:'HazMat Response',       desc:'Specialized chemical/hazmat unit',      icon:'â˜¢ï¸' },
              { id:'aerial',    label:'Aerial Response',       desc:'Ladder truck for high-rise buildings',  icon:'ðŸ—ï¸' },
            ].map(v => {
              const isSel = booking.vehicleType === v.id;
              return (
                <div key={v.id} onClick={() => update('vehicleType', v.id)} style={{
                  padding:'16px', borderRadius:'8px', cursor:'pointer',
                  background: isSel ? 'rgba(255,45,120,0.08)' : 'rgba(4,9,15,0.7)',
                  border: `1px solid ${isSel ? '#ff2d78' : 'rgba(255,45,120,0.15)'}`,
                  display:'flex', alignItems:'center', gap:'14px',
                  transition:'all 0.2s',
                  boxShadow: isSel ? '0 0 20px rgba(255,45,120,0.15)' : 'none',
                }}>
                  <div style={{ fontSize:'30px', lineHeight:1, flexShrink:0 }}>{v.icon}</div>
                  <div style={{ flex:1, minWidth:0 }}>
                    <div style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'13px', fontWeight:'700', color: isSel ? '#ff2d78' : '#e8f4fd', letterSpacing:'1.5px' }}>{v.label}</div>
                    <div style={{ fontSize:'12px', color:'#5a7a9a', marginTop:'2px' }}>{v.desc}</div>
                  </div>
                  {isSel && <div style={{ width:'10px', height:'10px', background:'#ff2d78', borderRadius:'50%', boxShadow:'0 0 8px #ff2d78', flexShrink:0 }} />}
                </div>
              );
            })}
          </div>
        );

      case 'location':
        return (
          <div style={{ display:'flex', flexDirection:'column', gap:'16px' }}>
            <p style={{ fontSize:'13px', color:'#5a7a9a' }}>Enter the fire location precisely</p>
            <Input label="Fire Location Address" type="text" value={booking.location.address} onChange={v => update('location', { address:v })} placeholder="Building name, street, landmark, city" icon="ðŸ“" />
            <div style={{ padding:'14px', background:'rgba(255,45,120,0.04)', border:'1px solid rgba(255,45,120,0.1)', borderRadius:'8px', fontSize:'12px', color:'#5a7a9a', lineHeight:'1.5', fontFamily:"'Share Tech Mono', monospace" }}>
              âš  Be as specific as possible â€” units will route to exact coordinates
            </div>
          </div>
        );

      case 'confirm':
        return (
          <div style={{ display:'flex', flexDirection:'column', gap:'16px' }}>
            {/* AI banner */}
            <div style={{
              padding:'16px', borderRadius:'8px', display:'flex', gap:'14px', alignItems:'flex-start',
              background:'linear-gradient(135deg, rgba(255,45,120,0.1) 0%, rgba(255,149,0,0.07) 100%)',
              border:'1px solid rgba(255,45,120,0.3)',
            }}>
              <div style={{ fontSize:'32px', filter:'drop-shadow(0 0 8px #ff2d78)', lineHeight:1, flexShrink:0 }}>ðŸ¤–</div>
              <div>
                <div style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'12px', color:'#ff2d78', letterSpacing:'2px', fontWeight:'700', marginBottom:'4px' }}>AI FIRE ANALYSIS</div>
                <div style={{ fontSize:'13px', color:'#e8f4fd', lineHeight:'1.5' }}>
                  Classified as <strong style={{ color:'#ff2d78' }}>{(booking.scale || 'MAJOR').toUpperCase()} FIRE</strong>. Deploying <strong>3 fire trucks + 1 HazMat</strong>. Spread risk: <strong style={{ color:'#ff9500' }}>High</strong>.
                </div>
              </div>
            </div>

            <div style={{ padding:'16px', background:'rgba(255,45,120,0.04)', border:'1px solid rgba(255,45,120,0.2)', borderRadius:'8px' }}>
              <div style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'12px', color:'#ff2d78', letterSpacing:'2px', marginBottom:'14px' }}>â—† DISPATCH SUMMARY</div>
              <SummaryRow label="Fire Type" value={booking.fireType?.toUpperCase() || 'â€”'} accentRGB="255,45,120" />
              <SummaryRow label="Scale" value={booking.scale?.toUpperCase() || 'â€”'} accentRGB="255,45,120" />
              <SummaryRow label="Building" value={booking.building?.type?.toUpperCase() || 'â€”'} accentRGB="255,45,120" />
              <SummaryRow label="Floors" value={booking.building?.floors ? `${booking.building.floors} floors` : 'â€”'} accentRGB="255,45,120" />
              <SummaryRow label="Trapped" value={booking.trapped || '0'} accentRGB="255,45,120" />
              <SummaryRow label="Response" value={booking.vehicleType?.toUpperCase() || 'â€”'} accentRGB="255,45,120" />
              <SummaryRow label="Location" value={booking.location?.address || 'â€”'} accentRGB="255,45,120" />
            </div>

            <Button onClick={handleDispatch} fullWidth size="lg" variant="secondary" loading={loading}>
              ðŸš’ DISPATCH FIRE UNITS NOW
            </Button>
          </div>
        );

      default: return null;
    }
  };

  return (
    <>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Exo+2:wght@700;800;900&family=Share+Tech+Mono&family=Rajdhani:wght@400;500;600;700&display=swap');
        *, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }
        @keyframes slideUp { from{opacity:0;transform:translateY(16px)} to{opacity:1;transform:translateY(0)} }
        @keyframes spin { from{transform:rotate(0deg)} to{transform:rotate(360deg)} }
        input { -webkit-appearance: none; }
        input[type=number]::-webkit-inner-spin-button,
        input[type=number]::-webkit-outer-spin-button { -webkit-appearance: none; }
        input[type=number] { -moz-appearance: textfield; }
      `}</style>

      <div style={{ minHeight:'100vh', minHeight:'100dvh', background:'#020408', color:'#e8f4fd', fontFamily:"'Rajdhani', sans-serif", position:'relative' }}>
        {/* Pink-tinted grid */}
        <div style={{ position:'fixed', inset:0, backgroundImage:`linear-gradient(rgba(255,45,120,0.02) 1px, transparent 1px), linear-gradient(90deg, rgba(255,45,120,0.02) 1px, transparent 1px)`, backgroundSize:'50px 50px', pointerEvents:'none', zIndex:0 }} />

        <NavBar
          onBack={() => currentStep > 0 ? setCurrentStep(s => s - 1) : onNavigate('home')}
          title="ðŸ”¥ Fire Emergency Report"
          subtitle={`STEP ${currentStep + 1} OF ${steps.length} Â· ${steps[currentStep].label.toUpperCase()}`}
          accentColor="#ff2d78"
          accentRGB="255,45,120"
        />

        <div style={{ maxWidth:'680px', margin:'0 auto', padding:'clamp(20px,4vw,32px) clamp(16px,4vw,24px)', position:'relative', zIndex:1 }}>
          <StepBar steps={steps} currentStep={currentStep} accentColor="#ff2d78" accentRGB="255,45,120" />
          <Card title={steps[currentStep].label} glow="secondary" style={{ marginBottom:'20px', animation:'slideUp 0.3s ease' }}>
            <StepContent />
          </Card>

          {steps[currentStep].id !== 'confirm' && (
            <div style={{ display:'flex', justifyContent:'flex-end' }}>
              <Button onClick={() => setCurrentStep(s => s + 1)} size="lg" variant="secondary">
                NEXT: {steps[currentStep + 1]?.label?.toUpperCase()} â†’
              </Button>
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default FireBookingScreen;
