import React, { useState, useEffect } from 'react';

// Screens — adjust paths to match your project structure
import LoginScreen from './screens/LoginScreen';
import HomeScreen from './screens/HomeScreen';
import MedicalBookingScreen from './screens/MedicalBookingScreen';
import FireBookingScreen from './screens/FireBookingScreen';
import TrackingScreen from './screens/TrackingScreen';

// Inject global CSS once — replaces the broken ./styles/theme import
const injectGlobalStyles = () => {
  if (document.getElementById('resqroute-global-styles')) return;
  const style = document.createElement('style');
  style.id = 'resqroute-global-styles';
  style.innerHTML = `
    @import url('https://fonts.googleapis.com/css2?family=Exo+2:wght@400;600;700;800;900&family=Share+Tech+Mono&family=Rajdhani:wght@400;500;600;700&display=swap');

    *, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

    html, body {
      background: #020408;
      color: #e8f4fd;
      font-family: 'Rajdhani', sans-serif;
      -webkit-tap-highlight-color: transparent;
      overflow-x: hidden;
    }

    input, button, select, textarea { font-family: inherit; }

    /* Prevent iOS input zoom — CRITICAL */
    input[type="text"],
    input[type="tel"],
    input[type="number"],
    input[type="email"],
    select, textarea { font-size: 16px !important; }

    /* Hide number spinners */
    input[type=number]::-webkit-inner-spin-button,
    input[type=number]::-webkit-outer-spin-button { -webkit-appearance: none; margin: 0; }
    input[type=number] { -moz-appearance: textfield; }

    /* Custom scrollbar */
    ::-webkit-scrollbar { width: 5px; }
    ::-webkit-scrollbar-track { background: #020408; }
    ::-webkit-scrollbar-thumb { background: rgba(0,245,255,0.2); border-radius: 3px; }
    ::-webkit-scrollbar-thumb:hover { background: rgba(0,245,255,0.4); }

    /* Shared keyframes */
    @keyframes float      { 0%,100%{transform:translateY(0)} 50%{transform:translateY(-8px)} }
    @keyframes slideUp    { from{opacity:0;transform:translateY(20px)} to{opacity:1;transform:translateY(0)} }
    @keyframes slideLeft  { from{opacity:0;transform:translateX(-14px)} to{opacity:1;transform:translateX(0)} }
    @keyframes pulseNeon  { 0%,100%{opacity:1} 50%{opacity:0.35} }
    @keyframes spin       { from{transform:rotate(0deg)} to{transform:rotate(360deg)} }
    @keyframes glowPulse  { 0%,100%{box-shadow:0 0 20px rgba(127,255,0,0.2)} 50%{box-shadow:0 0 40px rgba(127,255,0,0.6)} }
  `;
  document.head.appendChild(style);
};

const App = () => {
  const [screen, setScreen] = useState('login');
  const [screenParams, setScreenParams] = useState({});
  const [user, setUser] = useState(null);

  // Inject styles once on mount — safe, no duplicate injection
  useEffect(() => {
    injectGlobalStyles();
  }, []);

  const navigate = (screenName, params = {}) => {
    setScreen(screenName);
    setScreenParams(params);
    window.scrollTo({ top: 0, behavior: 'instant' });
  };

  const handleLogin = () => {
    setUser({ name: 'Rahul Sharma', phone: '+91 98765 43210' });
    navigate('home');
  };

  const renderScreen = () => {
    switch (screen) {
      case 'login':
        return <LoginScreen onLogin={handleLogin} />;
      case 'home':
        return <HomeScreen onNavigate={navigate} user={user} />;
      case 'medical-booking':
        return <MedicalBookingScreen onNavigate={navigate} />;
      case 'fire-booking':
        return <FireBookingScreen onNavigate={navigate} />;
      case 'tracking':
        return <TrackingScreen onNavigate={navigate} params={screenParams} />;
      default:
        return <LoginScreen onLogin={handleLogin} />;
    }
  };

  return (
    <div style={{ minHeight: '100vh', background: '#020408' }}>
      {renderScreen()}
    </div>
  );
};

export default App;
