import React, { useState, useEffect, useRef } from 'react';
import { BgGrid, Card, NavBar } from './SharedComponents';

/* ‚îÄ‚îÄ‚îÄ MINI LIVE MAP ‚îÄ‚îÄ‚îÄ */
const LiveMap = ({ type }) => {
  const canvasRef = useRef(null);
  const accentColor = type === 'fire' ? '#ff2d78' : '#00f5ff';
  const [vehiclePos, setVehiclePos] = useState({ x: 0.2, y: 0.7 });

  useEffect(() => {
    let frame;
    let t = 0;
    const animate = () => {
      t += 0.005;
      setVehiclePos({ x: 0.2 + Math.min(t, 0.6), y: 0.7 - Math.min(t, 0.4) });
      frame = requestAnimationFrame(animate);
    };
    frame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(frame);
  }, []);

  return (
    <div style={{ position:'relative', borderRadius:'8px', overflow:'hidden', background:'#040a10', border:`1px solid rgba(${type === 'fire' ? '255,45,120' : '0,245,255'},0.15)`, minHeight:'200px', aspectRatio:'16/9' }}>
      {/* Grid lines */}
      <svg width="100%" height="100%" style={{ position:'absolute', inset:0 }}>
        <defs>
          <pattern id="mapgrid" width="40" height="40" patternUnits="userSpaceOnUse">
            <path d="M 40 0 L 0 0 0 40" fill="none" stroke={`rgba(${type === 'fire' ? '255,45,120' : '0,245,255'},0.08)`} strokeWidth="0.5"/>
          </pattern>
        </defs>
        <rect width="100%" height="100%" fill="url(#mapgrid)"/>

        {/* Road lines */}
        <line x1="0%" y1="50%" x2="100%" y2="50%" stroke={`rgba(${type === 'fire' ? '255,45,120' : '0,245,255'},0.15)`} strokeWidth="2" strokeDasharray="8,8"/>
        <line x1="40%" y1="0%" x2="40%" y2="100%" stroke={`rgba(${type === 'fire' ? '255,45,120' : '0,245,255'},0.15)`} strokeWidth="2" strokeDasharray="8,8"/>
        <line x1="70%" y1="0%" x2="70%" y2="100%" stroke={`rgba(${type === 'fire' ? '255,45,120' : '0,245,255'},0.10)`} strokeWidth="1" strokeDasharray="4,4"/>

        {/* Route trail */}
        <line
          x1={`${vehiclePos.x * 100}%`} y1={`${vehiclePos.y * 100}%`}
          x2="82%" y2="25%"
          stroke={accentColor} strokeWidth="2" strokeDasharray="6,4" opacity="0.5"
        />

        {/* Vehicle dot (moving) */}
        <circle cx={`${vehiclePos.x * 100}%`} cy={`${vehiclePos.y * 100}%`} r="10" fill={`rgba(${type === 'fire' ? '255,45,120' : '0,245,255'},0.2)`}/>
        <circle cx={`${vehiclePos.x * 100}%`} cy={`${vehiclePos.y * 100}%`} r="5" fill={accentColor}/>

        {/* Destination pin */}
        <circle cx="82%" cy="25%" r="10" fill={`rgba(${type === 'fire' ? '255,45,120' : '0,245,255'},0.15)`}/>
        <circle cx="82%" cy="25%" r="5" fill="#7fff00"/>
        <circle cx="82%" cy="25%" r="14" fill="none" stroke="#7fff00" strokeWidth="1" opacity="0.4">
          <animate attributeName="r" from="10" to="22" dur="1.5s" repeatCount="indefinite"/>
          <animate attributeName="opacity" from="0.5" to="0" dur="1.5s" repeatCount="indefinite"/>
        </circle>
      </svg>

      {/* Labels */}
      <div style={{ position:'absolute', bottom:'10px', left:'10px', fontFamily:"'Share Tech Mono', monospace", fontSize:'10px', color:'#2a4a6a', letterSpacing:'1px' }}>LIVE MAP ¬∑ UPDATING</div>
      <div style={{ position:'absolute', top:'10px', right:'10px', padding:'4px 10px', background:'rgba(127,255,0,0.1)', border:'1px solid rgba(127,255,0,0.3)', borderRadius:'4px', fontFamily:"'Exo 2', sans-serif", fontSize:'10px', color:'#7fff00', fontWeight:'700', letterSpacing:'1.5px' }}>
        ‚óè GPS TRACKING
      </div>
      <div style={{ position:'absolute', bottom:'10px', right:'10px', padding:'4px 10px', background:`rgba(${type === 'fire' ? '255,45,120' : '0,245,255'},0.1)`, border:`1px solid ${accentColor}40`, borderRadius:'4px', fontFamily:"'Exo 2', sans-serif", fontSize:'10px', color:accentColor, fontWeight:'700', letterSpacing:'1px' }}>
        {type === 'fire' ? 'üöí FT-47' : 'üöë AMB-23'}
      </div>
    </div>
  );
};

/* ‚îÄ‚îÄ‚îÄ ETA DISPLAY ‚îÄ‚îÄ‚îÄ */
const ETADisplay = ({ eta, distance, vehicleId, type }) => {
  const accentColor = type === 'fire' ? '#ff2d78' : '#00f5ff';
  const accentRGB = type === 'fire' ? '255,45,120' : '0,245,255';
  return (
    <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fit, minmax(120px, 1fr))', gap:'12px' }}>
      {[
        { label:'ETA', value: eta === 0 ? 'ARRIVED' : `${eta} MIN`, color: eta === 0 ? '#7fff00' : accentColor },
        { label:'Distance', value: distance, color: accentColor },
        { label:'Vehicle ID', value: vehicleId, color: accentColor },
        { label:'Driver', value: type === 'fire' ? 'Suresh K.' : 'Rajan P.', color:'#e8f4fd' },
      ].map(item => (
        <div key={item.label} style={{ padding:'14px', background:`rgba(${accentRGB},0.04)`, border:`1px solid rgba(${accentRGB},0.12)`, borderRadius:'8px', textAlign:'center' }}>
          <div style={{ fontFamily:"'Share Tech Mono', monospace", fontSize:'clamp(16px,4vw,22px)', color:item.color, fontWeight:'700', marginBottom:'4px', textShadow:`0 0 10px rgba(${accentRGB},0.4)`, wordBreak:'break-word' }}>
            {item.value}
          </div>
          <div style={{ fontSize:'10px', color:'#2a4a6a', letterSpacing:'1.5px', textTransform:'uppercase', fontFamily:"'Exo 2', sans-serif" }}>
            {item.label}
          </div>
        </div>
      ))}
    </div>
  );
};

/* ‚îÄ‚îÄ‚îÄ MAIN TRACKING SCREEN ‚îÄ‚îÄ‚îÄ */
const statusFlow = ['dispatched','en_route','nearby','arrived'];
const statusLabels = {
  dispatched: { label:'UNIT DISPATCHED', color:'#ff9500', desc:'Emergency unit has been assigned and is leaving the station.' },
  en_route:   { label:'EN ROUTE',        color:'#00f5ff', desc:'Vehicle navigating to your location via green corridor.' },
  nearby:     { label:'APPROACHING',     color:'#7fff00', desc:'Less than 1km away. Keep path clear and line open.' },
  arrived:    { label:'ARRIVED',         color:'#7fff00', desc:'Emergency unit has reached your location.' },
};

const TrackingScreen = ({ onNavigate, params = {} }) => {
  const { type = 'medical', booking = {} } = params;
  const [status, setStatus] = useState('dispatched');
  const [showAlert, setShowAlert] = useState(true);

  const accentColor = type === 'fire' ? '#ff2d78' : '#00f5ff';
  const accentRGB   = type === 'fire' ? '255,45,120' : '0,245,255';
  const s = statusLabels[status];

  const eta = status === 'arrived' ? 0 : status === 'nearby' ? 1 : status === 'en_route' ? 5 : 8;
  const dist = status === 'arrived' ? '0 m' : status === 'nearby' ? '0.8 km' : '3.8 km';

  useEffect(() => {
    const timers = [
      setTimeout(() => setStatus('en_route'), 4000),
      setTimeout(() => setStatus('nearby'), 14000),
      setTimeout(() => setStatus('arrived'), 22000),
    ];
    return () => timers.forEach(clearTimeout);
  }, []);

  return (
    <>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Exo+2:wght@700;800;900&family=Share+Tech+Mono&family=Rajdhani:wght@400;500;600;700&display=swap');
        *, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }
        @keyframes pulseNeon { 0%,100%{opacity:1} 50%{opacity:0.4} }
        @keyframes slideUp { from{opacity:0;transform:translateY(16px)} to{opacity:1;transform:translateY(0)} }
        @keyframes glowPulse { 0%,100%{box-shadow:0 0 20px rgba(127,255,0,0.2)} 50%{box-shadow:0 0 40px rgba(127,255,0,0.6)} }
      `}</style>

      <div style={{ minHeight:'100vh', minHeight:'100dvh', background:'#020408', color:'#e8f4fd', fontFamily:"'Rajdhani', sans-serif", position:'relative' }}>
        <div style={{ position:'fixed', inset:0, backgroundImage:`linear-gradient(rgba(${accentRGB},0.02) 1px, transparent 1px), linear-gradient(90deg, rgba(${accentRGB},0.02) 1px, transparent 1px)`, backgroundSize:'50px 50px', pointerEvents:'none', zIndex:0 }} />

        {/* Lane clear alert banner */}
        {showAlert && (
          <div style={{
            position:'fixed', top:0, left:0, right:0, zIndex:200,
            padding:'12px clamp(16px,4vw,32px)',
            background:'linear-gradient(135deg, rgba(255,149,0,0.95) 0%, rgba(255,45,120,0.9) 100%)',
            display:'flex', alignItems:'center', justifyContent:'space-between', gap:'12px', flexWrap:'wrap',
          }}>
            <div style={{ display:'flex', alignItems:'center', gap:'12px', flex:1 }}>
              <span style={{ fontSize:'24px', flexShrink:0 }}>{type === 'fire' ? 'üöí' : 'üöë'}</span>
              <div style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'clamp(12px,3vw,15px)', fontWeight:'800', color:'#fff', letterSpacing:'2px', textTransform:'uppercase' }}>
                ‚ö† CLEAR THE LANE! {type === 'fire' ? 'FIRE TRUCK' : 'AMBULANCE'} APPROACHING
              </div>
            </div>
            <button onClick={() => setShowAlert(false)} style={{ background:'rgba(255,255,255,0.2)', border:'none', color:'#fff', padding:'6px 14px', borderRadius:'4px', cursor:'pointer', fontFamily:"'Exo 2', sans-serif", fontSize:'12px', fontWeight:'700', letterSpacing:'1px', flexShrink:0 }}>DISMISS</button>
          </div>
        )}

        {/* Header */}
        <div style={{
          padding:`${showAlert ? '72px' : '12px'} clamp(16px,4vw,32px) 12px`,
          borderBottom:`1px solid rgba(${accentRGB},0.15)`,
          background:'rgba(2,4,8,0.95)', backdropFilter:'blur(20px)',
          display:'flex', alignItems:'center', justifyContent:'space-between',
          position:'sticky', top:0, zIndex:100, gap:'12px', flexWrap:'wrap',
          transition:'padding 0.3s',
        }}>
          <div style={{ display:'flex', alignItems:'center', gap:'12px', flexShrink:0 }}>
            <div style={{ fontSize:'clamp(22px,5vw,30px)', filter:`drop-shadow(0 0 8px ${accentColor})` }}>
              {type === 'fire' ? 'üöí' : 'üöë'}
            </div>
            <div>
              <div style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'clamp(13px,3vw,16px)', fontWeight:'800', color:accentColor, letterSpacing:'2px', textTransform:'uppercase' }}>LIVE TRACKING</div>
              <div style={{ fontSize:'11px', color:'#2a4a6a', fontFamily:"'Share Tech Mono', monospace", letterSpacing:'1px' }}>
                {type === 'fire' ? 'FIRE UNIT FT-47' : 'AMBULANCE AMB-23'} ¬∑ #RRQ-{Math.floor(Math.random() * 90000 + 10000)}
              </div>
            </div>
          </div>

          {/* Status badge */}
          <div style={{
            display:'flex', alignItems:'center', gap:'8px',
            padding:'8px clamp(10px,2vw,16px)',
            background:`rgba(${accentRGB},0.08)`,
            border:`1px solid ${s.color}`,
            borderRadius:'6px',
            boxShadow:`0 0 20px rgba(${accentRGB},0.2)`,
            animation: status === 'arrived' ? 'glowPulse 1s ease infinite' : 'none',
            flexShrink:0,
          }}>
            <div style={{ width:'8px', height:'8px', background:s.color, borderRadius:'50%', boxShadow:`0 0 8px ${s.color}`, animation:'pulseNeon 1s ease infinite', flexShrink:0 }} />
            <span style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'clamp(11px,2.5vw,13px)', color:s.color, fontWeight:'800', letterSpacing:'1px', whiteSpace:'nowrap' }}>{s.label}</span>
          </div>
        </div>

        {/* MAIN */}
        <div style={{ maxWidth:'900px', margin:'0 auto', padding:'clamp(16px,3vw,28px) clamp(16px,4vw,24px)', display:'flex', flexDirection:'column', gap:'16px', position:'relative', zIndex:1 }}>

          {/* Status timeline */}
          <div style={{ padding:'16px', background:`rgba(${accentRGB},0.04)`, border:`1px solid rgba(${accentRGB},0.15)`, borderRadius:'8px' }}>
            <div style={{ display:'flex', gap:'0', overflowX:'auto' }}>
              {statusFlow.map((st, i) => {
                const isActive = statusFlow.indexOf(status) >= i;
                const isCurrent = status === st;
                return (
                  <div key={st} style={{ flex:'1 0 auto', display:'flex', flexDirection:'column', alignItems:'center', position:'relative', minWidth:'60px' }}>
                    {i < statusFlow.length - 1 && (
                      <div style={{ position:'absolute', top:'14px', left:'50%', right:'-50%', height:'2px', background: statusFlow.indexOf(status) > i ? accentColor : `rgba(${accentRGB},0.1)`, transition:'background 0.5s', zIndex:0 }} />
                    )}
                    <div style={{
                      width:'28px', height:'28px',
                      background: isActive ? `rgba(${accentRGB},0.2)` : 'rgba(4,9,15,0.8)',
                      border:`2px solid ${isActive ? accentColor : `rgba(${accentRGB},0.15)`}`,
                      borderRadius:'50%', display:'flex', alignItems:'center', justifyContent:'center',
                      zIndex:1, position:'relative',
                      boxShadow: isCurrent ? `0 0 15px rgba(${accentRGB},0.5)` : 'none',
                      transition:'all 0.5s',
                      flexShrink:0,
                    }}>
                      {isActive && <div style={{ width:'8px', height:'8px', background:accentColor, borderRadius:'50%' }} />}
                    </div>
                    <div style={{ marginTop:'7px', fontSize:'9px', letterSpacing:'0.5px', textTransform:'uppercase', fontFamily:"'Exo 2', sans-serif", color: isActive ? accentColor : '#2a4a6a', textAlign:'center', fontWeight: isCurrent ? '800' : '400', whiteSpace:'nowrap', overflow:'hidden', textOverflow:'ellipsis', maxWidth:'72px', padding:'0 4px' }}>
                      {statusLabels[st].label.split(' ')[0]}
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Live Map */}
          <Card title="Live Map" badge="REAL-TIME" glow={type === 'fire' ? 'secondary' : 'primary'} style={{ animation:'slideUp 0.3s ease' }}>
            <LiveMap type={type} />
          </Card>

          {/* ETA & Vehicle Info */}
          <Card title="ETA & Vehicle Info" glow={type === 'fire' ? 'secondary' : 'primary'}>
            <ETADisplay eta={eta} distance={dist} vehicleId={type === 'fire' ? 'FT-47' : 'AMB-23'} type={type} />
          </Card>

          {/* Status message */}
          <div style={{ padding:'14px 18px', background:`rgba(${accentRGB},0.04)`, border:`1px solid rgba(${accentRGB},0.15)`, borderRadius:'8px', fontSize:'clamp(13px,3vw,14px)', color:'#e8f4fd', lineHeight:'1.6' }}>
            <span style={{ color:accentColor, fontWeight:'700' }}>‚óÜ STATUS: </span>{s.desc}
          </div>

          {/* Driver contact */}
          <Card title="Driver Contact" glow="none">
            <div style={{ display:'flex', alignItems:'center', justifyContent:'space-between', gap:'12px', flexWrap:'wrap' }}>
              <div style={{ display:'flex', alignItems:'center', gap:'14px', flex:1, minWidth:'180px' }}>
                <div style={{ width:'46px', height:'46px', background:`rgba(${accentRGB},0.1)`, border:`1px solid rgba(${accentRGB},0.3)`, borderRadius:'50%', display:'flex', alignItems:'center', justifyContent:'center', fontSize:'22px', flexShrink:0 }}>üë®‚Äç‚úàÔ∏è</div>
                <div>
                  <div style={{ fontFamily:"'Exo 2', sans-serif", fontSize:'clamp(13px,3vw,15px)', fontWeight:'700', color:'#e8f4fd' }}>
                    {type === 'fire' ? 'Suresh Kumar' : 'Rajan Patel'}
                  </div>
                  <div style={{ fontSize:'12px', color:'#5a7a9a' }}>
                    {type === 'fire' ? 'Chief Fire Officer ¬∑ 12 yrs exp' : 'Paramedic ¬∑ Certified ALS'}
                  </div>
                </div>
              </div>
              <button style={{
                padding:'10px clamp(14px,3vw,20px)',
                background:`rgba(${accentRGB},0.1)`,
                border:`1px solid rgba(${accentRGB},0.4)`,
                borderRadius:'6px', color:accentColor,
                fontFamily:"'Exo 2', sans-serif", fontSize:'12px', fontWeight:'700',
                letterSpacing:'1.5px', cursor:'pointer', whiteSpace:'nowrap', flexShrink:0,
              }}>
                üìû CALL
